$(function(){$(".table").pagination({updateDataURL:"/backend/art/articlelist",delDataURL:"/backend/art/remove/toTrash",process:function(t){for(var a=t.data.artCount,d="",e=t.data.artInfo.arcList,r=0;r<e.length;r++){var n=e[r],l=r+1,i=n.id;d+='<tr><th scope="row">'+l+"</th><td>"+n.title+'</td><td><a class="badge badge-light" href="?by[type_id]='+n.type.id+'">'+n.type.name+"</a></td><td>"+n.read+"</td><td>"+n.time_create+"</td><td>"+n.time_update+'</td><td><a href="updatearticle/'+i+'" class="btn btn-teal btn-block">修改</a><a href="javascript:void(0);" data-toggle="modal" data-target="#delArcModal" class="btn btn-dark btn-block del-art-btn"  data-artid='+i+">删除</a></td></tr>"}return{Count:a,addHtml:d}}})});